import logo from './logo.svg';
import './App.css';
import Button from '@mui/material/Button';
import axios from 'axios';
import React, { useState } from 'react';

function App() {
    const [otp, setOtp] = useState('');
    const handleOTPGeneration = async () => {
        try {
            const response = await axios.post('https://localhost:7231/api/generate-otp');
            console.log(response.data);
            setOtp(response.data.otp);

        } catch (error) {
            console.error('Error generating otp: ', error);
        }
    }



  return (
      <div className="App-header">
          <h1>OTP Generation</h1>
          <div className="digitContainers">
              {otp && otp.split('').map((digit, index) => (
                  <div key={index} className="Key">{digit.toLocaleUpperCase}</div>
              ))}
          </div>


          <Button onClick={handleOTPGeneration} className="GenButton" variant="outlined">Generate New</Button>


          <p className="TimeText">Time Left To Use: </p>
      </div>
  );
}

export default App;
