import logo from './logo.svg';
import './App.css';
import Button from '@mui/material/Button';
import axios from 'axios';
import React, { useState, useEffect } from 'react';


function App() {
    const [otp, setOtp] = useState('');
    const [timeLeft, setTimeLeft] = useState(60);
    const [otpObj, setOtpObj] = useState(null);


    const handleOTPGeneration = async () => {
        try {
            const response = await axios.post('https://localhost:7231/api/generate-otp');
            console.log(response.data);

            const newOtp = response.data.otp;
            setOtp(newOtp);

            setTimeLeft(60);

            const newOtpObj = new OTP(newOtp);


        } catch (error) {
            console.error('Error generating otp: ', error);
        }
    }

    useEffect(() => {
        // Set up the timer to decrement time left every second
        const timer = setTimeout(() => {
            if (timeLeft > 0) {
                setTimeLeft(prevTime => prevTime - 1);
            }
        }, 1000);

        if (otpObj) {
            otpObj.IsOTPValid(timeLeft);
        }

        // Clear the timer when time left reaches 0 or when the component unmounts
        return () => clearTimeout(timer);
    }, [timeLeft]);



  return (
      <div className="App-header">
          <h1>OTP Generation</h1>
          <div className="digitContainers">
              {otp && otp.split('').map((digit, index) => (
                  <div key={index} className="Key">{digit}</div>
              ))}
          </div>


          <Button onClick={handleOTPGeneration} className="GenButton" variant="outlined">Generate New</Button>


          <p className="TimeText">Time Left To Use: {timeLeft} seconds</p>
      </div>
  );
}

export default App;
